#tlvInput {
    font-size: 1rem;
    min-height: 120px;
    color: #555;
    background-color: #fcfcfc;
    border: 1px solid #ccc;
    border-radius: 5px;
    width: 100%;
    resize: none;
    box-sizing: border-box;
}
#parseButton {
    width: 100%;
    margin-top: 15px;
    border: none;
}
#parseButton::before {
    content: none;
}
#resultsTable {
    word-break: break-all;
  	box-sizing: border-box;
  	width: 100%;
  	text-align: center;
}
#resultsTable tr {
    border-bottom: 1px solid var(--inside-accent-color-08);
    color: black;
}
#resultsTable thead {
    background-color: var(--inside-accent-color);
    color: white;
}
#results .error {
    color: #ef5350;
    font-weight: bold;
    text-align: center;
    padding: 15px;
    border: 1px solid #ef5350;
    border-radius: 5px;
    background-color: #ffebee;
}





    Enter a Thread MeshCoP TLV hex string below to parse it into a human-readable format.


        Parse TLV






(function() {
    const MESHCOP_TLV_TYPE = {
        0: "CHANNEL", 1: "PANID", 2: "EXTPANID", 3: "NETWORKNAME", 4: "PSKC",
        5: "NETWORKKEY", 6: "NETWORK_KEY_SEQUENCE", 7: "MESHLOCALPREFIX", 8: "STEERING_DATA",
        9: "BORDER_AGENT_RLOC", 10: "COMMISSIONER_ID", 11: "COMM_SESSION_ID", 12: "SECURITYPOLICY",
        13: "GET", 14: "ACTIVETIMESTAMP", 15: "COMMISSIONER_UDP_PORT", 16: "STATE",
        17: "JOINER_DTLS", 18: "JOINER_UDP_PORT", 19: "JOINER_IID", 20: "JOINER_RLOC",
        21: "JOINER_ROUTER_KEK", 32: "PROVISIONING_URL", 33: "VENDOR_NAME_TLV",
        34: "VENDOR_MODEL_TLV", 35: "VENDOR_SW_VERSION_TLV", 36: "VENDOR_DATA_TLV",
        37: "VENDOR_STACK_VERSION_TLV", 48: "UDP_ENCAPSULATION_TLV", 49: "IPV6_ADDRESS_TLV",
        51: "PENDINGTIMESTAMP", 52: "DELAYTIMER", 53: "CHANNELMASK", 54: "COUNT",
        55: "PERIOD", 56: "SCAN_DURATION", 57: "ENERGY_LIST", 59: "THREAD_DOMAIN_NAME",
        74: "WAKEUP_CHANNEL", 128: "DISCOVERYREQUEST", 129: "DISCOVERYRESPONSE", 241: "JOINERADVERTISEMENT",
    };

    const parseButton = document.getElementById('parseButton');
    const tlvInput = document.getElementById('tlvInput');
    const resultsDiv = document.getElementById('results');

    const parseTlv = () => {
        const hexString = tlvInput.value.trim().replace(/\s+/g, '');
        resultsDiv.innerHTML = '';

        if (!hexString) return;

        if (hexString.length % 2 !== 0 || !/^[0-9a-fA-F]*$/.test(hexString)) {
            resultsDiv.innerHTML = 'Invalid hex string. Please ensure it has an even number of characters and contains only 0-9 and A-F.';
            return;
        }

        try {
            const data = Array.from(hexString.match(/.{1,2}/g), byte => parseInt(byte, 16));
            const parsedTlvs = [];
            let pos = 0;

            while (pos < data.length) {
                if (pos + 2 > data.length) throw new Error("Incomplete TLV structure.");
                const tag = data[pos++];
                const len = data[pos++];
                if (pos + len > data.length) throw new Error("Invalid length field.");

                const valueBytes = data.slice(pos, pos + len);
                pos += len;

                const typeName = MESHCOP_TLV_TYPE[tag] || "UNKNOWN";
                let value;

                if (tag === 3) { // NETWORKNAME
                    value = new TextDecoder().decode(Uint8Array.from(valueBytes));
                } else {
                    const hexValue = '0x' + valueBytes.map(b => b.toString(16).padStart(2, '0')).join('');
                    //Check for CHANNEL tag
                    if (tag === 0 && valueBytes.length > 0) {
                        const decValue = parseInt(valueBytes.map(b => b.toString(16).padStart(2, '0')).join(''), 16);
                        value = `${hexValue} (${decValue})`;
                    } else {
                        value = hexValue;
                    }
                }

                parsedTlvs.push({ tag, typeName, len, value });
            }

            if (parsedTlvs.length > 0) {
                let tableHtml = 'Type (dec)Type (name)LengthValue';
                parsedTlvs.forEach(tlv => {
                    tableHtml += `${tlv.tag}${tlv.typeName}${tlv.len}${tlv.value}`;
                });
                tableHtml += '';
                resultsDiv.innerHTML = tableHtml;
            } else {
                 resultsDiv.innerHTML = 'No TLVs found in the provided string.';
            }
        } catch (error) {
            resultsDiv.innerHTML = `Error: ${error.message}`;
        }
    };

    if (parseButton) {
        parseButton.addEventListener('click', parseTlv);
    }
})();
